import React, { useState } from "react";
import axios from "axios";
import "./App.css";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [context, setContext] = useState([]);
  const [darkMode, setDarkMode] = useState(false);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMsg = { sender: "You", text: input };
    setMessages((prev) => [...prev, userMsg]);

    try {
      const response = await axios.post("http://localhost:8000/mcp/infer", {
        prompt: input,
        context: context,
      });

      const botMsg = {
        sender: "Bot",
        text: linkify(response.data.response), // âœ… make links clickable
        meta: {
          sentiment: response.data.sentiment,
          source: response.data.source,
        },
      };

      setMessages((prev) => [...prev, botMsg]);
      setContext(response.data.context || []);
      setInput("");
    } catch (err) {
      console.error("Error:", err);
      setMessages((prev) => [
        ...prev,
        { sender: "Bot", text: "âŒ Error contacting server." },
      ]);
    }
  };

  // âœ… Converts URLs to anchor links
  const linkify = (text) => {
    const urlRegex = /(https?:\/\/[^\s]+)/g;
    return text.replace(urlRegex, (url) => {
      return `<a href="${url}" target="_blank" rel="noopener noreferrer">${url}</a>`;
    });
  };

  return (
    <div className={`chat-container ${darkMode ? "dark" : "light"}`}>
      <div className="chat-header">
        <h2>ðŸ¤– ChatBrain</h2>
        <button onClick={() => setDarkMode(!darkMode)}>
          {darkMode ? "ðŸŒž Light Mode" : "ðŸŒ™ Dark Mode"}
        </button>
      </div>

      <div className="chat-box">
        {messages.map((msg, i) => (
          <div key={i} className={`msg ${msg.sender === "You" ? "you" : "bot"}`}>
            <strong>{msg.sender}:</strong>
            {msg.sender === "Bot" ? (
              <div
                className="msg-text"
                dangerouslySetInnerHTML={{ __html: msg.text }}
              />
            ) : (
              <span>{msg.text}</span>
            )}
            {msg.meta && (
              <div className="meta">
                <small>Sentiment: {msg.meta.sentiment}</small>
                <small>Source: {msg.meta.source}</small>
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="chat-input">
        <input
          placeholder="Type something..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button onClick={sendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
